                  <Input type="text" name="userCity" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userCity)} onChange={this.userCity} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
