import { data, event } from "jquery";
import React from "react";
import { Button, Col, Form, Input, InputGroup } from 'reactstrap';
import { Link, Route, Routes, BrowserRouter } from 'react-router-dom';
import CustomerHome from "./CustomerHome";
import MaidHome from "./MaidHome";
import personal from './Images/personal.jpg';

const validContactRegex = RegExp(
  / \+91-[\d]{10}$/
);

const ValidateForm = errors => {
  let valid = true;
  Object.values(errors).forEach(val => val.length > 0 && (valid = false));
  return valid;
};

class MaidProfile extends React.Component {


  constructor(props) {
    super(props);
    const token = (localStorage.getItem("loggedincustomer"));
    let loggedIn = true;
    this.state = {
      maidId: '',
      maidName: '',
      maidUsername: '',
      maidPassword: '',
      maidAge: '',
      maidMobileNo: '',
      maidEmailId: '',
      maidAddress: '',
      maidCity: '',
      maidPincode: '',
      maidAdharCard: '',
      maidPoliceVerificationCertificate: '',
      monthCharges: '',
      maidExperience: '',


      errors: {
        maidMobileNo: ''

      }
    }
  }

  maidName = (event) => {

    this.setState({ Name: event.target.value })

  }

  maidUsername = (event) => {

    this.setState({ MaidName: event.target.value })

  }
  maidPassword = (event) => {

    this.setState({ Password: event.target.value })

  }


  maidAge = (event) => {

    this.setState({ Age: event.target.value })

  }



  maidMobileNo = (event) => {

    this.setState({ MobileNo: event.target.value })

  }

  maidEmailId = (event) => {

    this.setState({ EmailID: event.target.value })

  }
  maidAddress = (event) => {

    this.setState({ Address: event.target.value })

  }
  maidCity = (event) => {

    this.setState({ City: event.target.value })

  }
  maidAdharCard = (event) => {

    this.setState({ AdharCard: event.target.value })

  }

  maidPoliceVerificationCertificate = (event) => {

    this.setState({ PVC: event.target.value })

  }


  monthCharges = (event) => {

    this.setState({ monthCharges: event.target.value })

  }

  maidExperience = (event) => {

    this.setState({ Experience: event.target.value })

  }


  handleChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    let errors = this.state.errors;

    switch (name) {

      case 'maidMobileNo':
        errors.userMobileNo =
          value.length === 10
            ? ''
            : 'Contact No must be 10 digits long!';
        break;




      default:
        break;
    }



    this.setState({ errors, [name]: value });
  }

  cancel = (event) => {
    // window.location="/CustomerHome";

  }


  update = (event) => {
    event.preventDefault();

    if (ValidateForm(this.state.errors)) {
      console.info('Valid Form')
    }
    else {
      console.error('Invalid Form')
    }

    const requestOption = {
      method: 'post',

      headers: {

        'Accept': 'application/json',
        'Content-Type': 'application/json'

      },
      "body": JSON.stringify({
        maidId: (JSON.parse(localStorage.getItem('loggedincustomer')).maidId),
        maidName: (JSON.parse(localStorage.getItem('loggedincustomer')).maidName),
        maidUsername: (JSON.parse(localStorage.getItem('loggedincustomer')).maidUsername),
        maidPassword: (JSON.parse(localStorage.getItem('loggedincustomer')).maidPasword),
        maidAge: (JSON.parse(localStorage.getItem('loggedincustomer')).maidAge),
        maidMobileNo: (JSON.parse(localStorage.getItem('loggedincustomer')).maidMobileNo),
        maidEmailId: (JSON.parse(localStorage.getItem('loggedincustomer')).maidEmailId),
        maidAddress: (JSON.parse(localStorage.getItem('loggedincustomer')).maidAddress),
        maidCity: (JSON.parse(localStorage.getItem('loggedincustomer')).maidCity),
        maidPincode: (JSON.parse(localStorage.getItem('loggedincustomer')).maidPincode),
        maidAdharCard: (JSON.parse(localStorage.getItem('loggedincustomer')).maidAdharCard),
        maidPoliceVerificationCertificate: (JSON.parse(localStorage.getItem('loggedincustomer')).maidPoliceVerificationCertificate),
        monthCharges: (JSON.parse(localStorage.getItem('loggedincustomer')).monthCharges),
        maidExperience: (JSON.parse(localStorage.getItem('loggedincustomer')).maidExperience),


      })
    };

    fetch('http://localhost:8080/maid/maidinsert', requestOption)
      .then(res => {
        if (res.status === 200) {
          alert("updated successfully");
          //alert("Name : " + this.state.c_name  );
          //alert("Password : "+this.state.password);
          window.location = "/MaidHome";
          //this.props.history.push("/login");
        }
        else {
          alert("OOPS! Registration Failed !");
        }
      })

  }

  render() {


    const { errors } = this.state;



    return (

      <div className="container-fluid" style={{ backgroundImage: `url(${personal})`, height: '1000px', backgroundPosition: "center", backgroundRepeat: "no-repeat", backgroundSize: 'cover', color: 'black' }} >
        {<style>{'body{background-color:#DFDFDE}'}</style>}
        <div class="nav">&nbsp;&nbsp;
          <button><a href="MaidHome">BACK</a></button>
        </div>
        <h4 style={{ fontFamily: 'arial', fontSize: 36 }}><b><i>Welcome {JSON.parse(localStorage.getItem('loggedincustomer')).userName}</i></b></h4>

        <div id="form" class="row" className="mb-3 pageheading" >
          <br />
          <h2>UPDATE PERSONAL INFORMATION</h2>
        </div>
        <br />

        <center>
          <Col md="9" lg="7" xl="6">


            <Form noValidate>


              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Name : </label>&nbsp; &nbsp;
                  <Input type="text" name="Name" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).maidName)} disabled={true} noValidate required />
                </InputGroup>
              </div>

              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>User Name : </label>&nbsp; &nbsp;
                  <Input type="text" name="MaidName" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).maidUsername)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Password : </label>&nbsp; &nbsp;
                  <Input type="text" name="Password" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).maidPassword)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Age : </label> &nbsp; &nbsp;
                  <Input type="number" name="Age" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).maidAge)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Mobile NO : </label> &nbsp; &nbsp;
                  <Input type="text" name="MobileNo" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).maidMobileNo)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Email ID : </label> &nbsp; &nbsp;
                  <Input type="text" name="EmailID" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).maidEmailId)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Address : </label> &nbsp; &nbsp;
                  <Input type="text" name="Address" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).maidAddress)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>City : </label> &nbsp; &nbsp;
                  <Input type="text" name="City" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).maidCity)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Pincode : </label> &nbsp; &nbsp;
                  <Input type="text" name="Pincode" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).maidPincode)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Adhar Card : </label> &nbsp; &nbsp;
                  <Input type="text" name="AdharCard" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).maidAdharCard)} disabled={true} noValidate required />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Police Verification Certificate : </label> &nbsp; &nbsp;
                  <Input type="text" name="PVC" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).maidPoliceVerificationCertificate)} disabled={true} noValidate required />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Month Charge: </label> &nbsp; &nbsp;
                  <Input type="number" name="monthCharges" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).monthCharges)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Experience : </label> &nbsp; &nbsp;
                  <Input type="number" name="Experience" placeholder={(JSON.parse(localStorage.getItem('loggedinmaid')).maidExperience)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <Button onClick={(event) => { this.update(event) }} color="success" >UPDATE</Button>

            </Form>
            <br /><br /><br /><br /><br /><br /><br /><br />
          </Col>
        </center>
        <Routes>
          <Route path="/MaidHome" element={<MaidHome />} />
        </Routes>

      </div>
    );

  }

}

export default MaidProfile;
