import { data, event } from "jquery";
import React from "react";
import { Button, Col, Form, Input, InputGroup } from 'reactstrap';
import { Link, Route, Routes, BrowserRouter } from 'react-router-dom';
import CustomerHome from "./CustomerHome";
import personal from './Images/personal.jpg';

const validContactRegex = RegExp(
  / \+91-[\d]{10}$/
);

const ValidateForm = errors => {
  let valid = true;
  Object.values(errors).forEach(val => val.length > 0 && (valid = false));
  return valid;
};

class Profile extends React.Component {


  constructor(props) {
    super(props);
    const token = (localStorage.getItem("loggedincustomer"));
    let loggedIn = true;
    this.state = {
      userId: '',
      userName: '',
      userUsername: '',
      userPassword: '',
      userGender: '',
      userFamilyMembers: '',
      userRooms: '',
      userMobileNo: '',
      userEmailId: '',
      userAddress: '',
      userCity: '',
      userPincode: '',
      userAdharCard: '',


      errors: {
        userMobileNo: ''

      }
    }
  }

  userName = (event) => {

    this.setState({ Name: event.target.value })

  }

  userUsername = (event) => {

    this.setState({ UserName: event.target.value })

  }
  userPassword = (event) => {

    this.setState({ Password: event.target.value })
  }

  userGender = (event) => {

    this.setState({ Gender: event.target.value })

  }


  userFamilyMembers = (event) => {

    this.setState({ FamilyMembers: event.target.value })

  }

  userRooms = (event) => {

    this.setState({ rooms: event.target.value })

  }

  userMobileNo = (event) => {

    this.setState({ MobileNo: event.target.value })

  }

  userEmailId = (event) => {

    this.setState({ EmailID: event.target.value })

  }
  userAddress = (event) => {

    this.setState({ Address: event.target.value })

  }
  userCity = (event) => {

    this.setState({ City: event.target.value })

  }
  userPincode = (event) => {

    this.setState({ Pincode: event.target.value })

  }

  userAdharCard = (event) => {

    this.setState({ AdharCard: event.target.value })

  }

  handleChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    // event.target.value()
    let errors = this.state.errors;

    switch (name) {

      case 'userMobileNo':
        errors.userMobileNo =
          value.length === 10
            ? ''
            : 'Contact No must be 10 digits long!';
        break;

      default:
        break;
    }



    this.setState({ errors, [name]: value });
  }

  cancel = (event) => {
    // window.location="/CustomerHome";

  }


  update = (event) => {
    event.preventDefault();

    if (ValidateForm(this.state.errors)) {
      console.info('Valid Form')
    }
    else {
      console.error('Invalid Form')
    }
    
    const userId= (JSON.parse(localStorage.getItem('loggedincustomer')).userId);

    const requestOption = {
      method: 'post',

      headers: {

        'Accept': 'application/json',
        'Content-Type': 'application/json'

      },
      "body": JSON.stringify({
        userId: (JSON.parse(localStorage.getItem('loggedincustomer')).userId),
        userName: (JSON.parse(localStorage.getItem('loggedincustomer')).userName),
        userUsername: (JSON.parse(localStorage.getItem('loggedincustomer')).userUsername),
        userGender: (JSON.parse(localStorage.getItem('loggedincustomer')).userGender),
        userPassword: (JSON.parse(localStorage.getItem('loggedincustomer')).userPasword),
        userFamilyMembers: (JSON.parse(localStorage.getItem('loggedincustomer')).userFamilyMembers),
        userRooms: this.state.userRooms,
        userMobileNo: (JSON.parse(localStorage.getItem('loggedincustomer')).userMobileNo),
        userEmailId: (JSON.parse(localStorage.getItem('loggedincustomer')).userEmailId),
        userAddress: this.state.userAddress,
        userCity: (JSON.parse(localStorage.getItem('loggedincustomer')).userCity),
        userPincode: (JSON.parse(localStorage.getItem('loggedincustomer')).userPincode),
        userAdharCard: (JSON.parse(localStorage.getItem('loggedincustomer')).userAdharCard),

      })
    };

    fetch('http://localhost:8080/user/userupdate'+userId, requestOption)
      .then(res => {
        if (res.status === 200) {
          alert("updated successfully");
          //alert("Name : " + this.state.c_name  );
          //alert("Password : "+this.state.password);
          window.location = "/CustomerHome";
          //this.props.history.push("/login");
        }
        else {
          alert("OOPS! Registration Failed !");
        }
      })

  }

  render() {


    const { errors } = this.state;



    return (

      <div className="container-fluid" style={{ backgroundImage: `url(${personal})`, height: '1000px', backgroundPosition: "center", backgroundRepeat: "no-repeat", backgroundSize: 'cover', color: 'black' }} >
        <div class="nav">&nbsp;&nbsp;
          <button><a href="CustomerHome">BACK</a></button>
        </div>
        <h4 style={{ fontFamily: 'arial', fontSize: 36 }}><b>Welcome {JSON.parse(localStorage.getItem('loggedincustomer')).userName}</b></h4>
        <br />
        <div id="form" class="row" className="mb-3 pageheading" >

          <h2>UPDATE PERSONAL INFORMATION</h2>
        </div>


        <center>
          <Col md="9" lg="7" xl="6">


            <Form noValidate>


              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Name : </label>&nbsp; &nbsp;
                  <Input type="text" name="Name" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userName)} disabled={true} noValidate required />
                </InputGroup>
              </div>

              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>User Name : </label>&nbsp; &nbsp;
                  <Input type="text" name="UserName" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userUsername)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Password : </label>&nbsp; &nbsp;
                  <Input type="text" name="Password" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userPassword)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Gender : </label>&nbsp; &nbsp;
                  <Input type="text" name="Gender" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userGender)} disabled={true} noValidate required />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Family Members : </label> &nbsp; &nbsp;
                  <Input type="text" name="FamilyMembers" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userFamilyMembers)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Rooms : </label> &nbsp; &nbsp;
                  <Input type="text" name="rooms" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userRooms)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Mobile NO : </label> &nbsp; &nbsp;
                  <Input type="text" name="MobileNo" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userMobileNo)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Email ID : </label> &nbsp; &nbsp;
                  <Input type="text" name="EmailID" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userEmailId)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Address : </label> &nbsp; &nbsp;
                  <Input type="text" name="Address" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userAddress)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>City : </label> &nbsp; &nbsp;
                  <Input type="text" name="City" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userCity)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Pincode : </label> &nbsp; &nbsp;
                  <Input type="text" name="Pincode" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userPincode)} onChange={this.handleChange} noValidate />
                </InputGroup>
              </div>

              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <InputGroup className="mb-3">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Adhar Card : </label>&nbsp; &nbsp;
                  <Input type="text" name="AdharCard" placeholder={(JSON.parse(localStorage.getItem('loggedincustomer')).userAdharCard)} disabled={true} noValidate required />
                </InputGroup>
              </div>

              <Button onClick={(event) => { this.update(event) }} color="success" >UPDATE</Button>
            </Form>
          </Col>
        </center>
        <Routes>
          <Route path="/CustomerHome" element={<CustomerHome />} />
        </Routes>

      </div>
    );

  }

}

export default Profile;
