import { data, event } from "jquery";
import React from "react";
import { Button, Col, Form, Input, InputGroup } from 'reactstrap';
import { Link, Route, Routes, BrowserRouter } from 'react-router-dom';
import MaidHome from "./MaidHome";
import personal from './Images/personal.jpg';

const validContactRegex = RegExp(
  / \+91-[\d]{10}$/
);

const ValidateForm = errors => {
  let valid = true;
  Object.values(errors).forEach(val => val.length > 0 && (valid = false));
  return valid;
};

class UpdateMaid extends React.Component {
  constructor(props) {
    super(props);
    const token = (localStorage.getItem("updatemaid"));
    let loggedIn = true;
    this.state = {
      maidName: '',
      maidUsername: '',
      maidPassword: '',
      maidAge: '',
      maidMobileNo: '',
      maidEmailId: '',
      maidAddress: '',
      maidCity: '',
      maidPincode: '',
      maidAdharCard: '',
      maidPoliceVerificationCertificate: '',
      maidExtraChargePerRoom: '',
      maidExtraChargePerMember: '',
      maidExperience: '',

      errors: {
        maidMobileNo: ''
      }
    }
  }
  maidName = (event) => {

    this.setState({ maidName: event.target.value })

  }
  maidUsername = (event) => {

    this.setState({ maidUsername: event.target.value })

  }
  maidPassword = (event) => {

    this.setState({ maidPassword: event.target.value })

  }
  maidAge = (event) => {

    this.setState({ maidAge: event.target.value })

  }
  maidMobileNo = (event) => {

    this.setState({ maidMobileNo: event.target.value })

  }
  maidEmailId = (event) => {

    this.setState({ maidEmailId: event.target.value })

  }
  maidAddress = (event) => {

    this.setState({ maidAddress: event.target.value })

  }

  maidCity = (event) => {

    this.setState({ maidCity: event.target.value })

  }

  maidPincode = (event) => {

    this.setState({ maidPincode: event.target.value })

  }
  maidAdharCard = (event) => {

    this.setState({ maidAdharCard: event.target.value })

  }
  maidPoliceVerificationCertificate = (event) => {

    this.setState({ maidPoliceVerificationCertificate: event.target.value })

  }
  maidExtraChargePerMember = (event) => {

    this.setState({ maidExtraChargePerMember: event.target.value })

  }
  maidExtraChargePerRoom = (event) => {

    this.setState({ maidExtraChargePerRoom: event.target.value })

  }
  maidExperience = (event) => {

    this.setState({ maidExperience: event.target.value })

  }

  handleChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    let errors = this.state.errors;

    switch (name) {

      case 'maidMobileNo':
        errors.maidMobileNo =
          value.length === 10
            ? ''
            : 'Contact No must be 10 digits long!';
        break;

      default:
        break;
    }

    this.setState({ errors, [name]: value });
  }

  cancel = () => {
    // this.props.history.push('/MaidHome');
  }


  update = (event) => {
    event.preventDefault();



    const requestOption = {
      method: 'post',

      headers: {

        'Accept': 'application/json',
        'Content-Type': 'application/json'

      },
      "body": JSON.stringify({
        maidId: (JSON.parse(localStorage.getItem('updatemaid')).maidId),
        maidName: (JSON.parse(localStorage.getItem('updatemaid')).maidName),
        maidUsername: (JSON.parse(localStorage.getItem('updatemaid')).maidUsername),
        maidAge: (JSON.parse(localStorage.getItem('updatemaid')).maidAge),
        maidMobileNo: (JSON.parse(localStorage.getItem('updatemaid')).maidMobileNo),
        maidEmailId: (JSON.parse(localStorage.getItem('updatemaid')).maidEmailId),
        maidAddress: this.state.maidAddress,
        maidCity: (JSON.parse(localStorage.getItem('updatemaid')).maidCity),
        maidPincode: (JSON.parse(localStorage.getItem('updatemaid')).maidPincode),
        maidAdharCard: (JSON.parse(localStorage.getItem('updatemaid')).maidAdharCard),
        maidPoliceVerificationCertificate: (JSON.parse(localStorage.getItem('updatemaid')).maidPoliceVerificationCertificate),
        maidExtraChargePerRoom: (JSON.parse(localStorage.getItem('updatemaid')).maidExtraChargePerRoom),
        maidExtraChargePerMember: (JSON.parse(localStorage.getItem('updatemaid')).maidExtraChargePerMember),
        maidExperience: (JSON.parse(localStorage.getItem('updatemaid')).maidExperience),
      })
    };


    console.log(this.state.address);
    fetch('http://localhost:8080/maid/maidinsert', requestOption)
      .then(res => {
        if (res.status === 200) {
          alert("updated successfully");
          //alert("Name : " + this.state.c_name  );
          //alert("Password : "+this.state.password);
          window.location = "/AdminHome";
          //this.props.history.push("/login");
        }
        else {
          alert("OOPS! Failed !");
          window.location = "#";
        }
      })

  }

  render() {

    const { errors } = this.state;

    return (
      <div className="container-fluid" style={{ backgroundImage: `url(${personal})`, height: '895px', backgroundRepeat: "no-repeat", backgroundSize: 'cover', color: 'white' }} >
        {<style>{'body{background-color:#DFDFDE}'}</style>}
        <h4 style={{ fontFamily: 'arial', fontSize: 36 }}><b><i>Welcome Admin </i></b></h4>

        <div id="form" class="row" className="mb-3 pageheading" >

          <h2>UPDATE PERSONAL INFORMARTION</h2>
        </div>

        <center>
          <Col md="9" lg="7" xl="6">


            <Form noValidate>


              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Name : </label>&nbsp; &nbsp;
                  <Input type="text" name="maidName" value={(JSON.parse(localStorage.getItem('updatemaid')).maidName)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Username : </label>&nbsp; &nbsp;
                  <Input type="text" name="maidUsername" value={(JSON.parse(localStorage.getItem('updatemaid')).maidUsername)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Password : </label>&nbsp; &nbsp;
                  <Input type="text" name="maidPassword" value={(JSON.parse(localStorage.getItem('updatemaid')).maidPassword)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Age : </label>&nbsp; &nbsp;
                  <Input type="number" name="maidAge" value={(JSON.parse(localStorage.getItem('updatemaid')).maidAge)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Mobile NO : </label>&nbsp; &nbsp;
                  <Input type="text" name="maidMobileNo" value={(JSON.parse(localStorage.getItem('updatemaid')).maidMobileNo)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Email ID : </label>&nbsp; &nbsp;
                  <Input type="text" name="maidEmailId" value={(JSON.parse(localStorage.getItem('updatemaid')).maidEmailId)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Address : </label>&nbsp; &nbsp;
                  <Input type="text" name="maidAddress" value={(JSON.parse(localStorage.getItem('updatemaid')).maidAddress)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>City : </label>&nbsp; &nbsp;
                  <Input type="text" name="maidCity" value={(JSON.parse(localStorage.getItem('updatemaid')).maidCity)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Pincode : </label>&nbsp; &nbsp;
                  <Input type="text" name="maidPincode" value={(JSON.parse(localStorage.getItem('updatemaid')).maidPincode)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Adhar Card : </label>&nbsp; &nbsp;
                  <Input type="text" name="maidAdharCard" value={(JSON.parse(localStorage.getItem('updatemaid')).maidAdharCard)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Police Verification Certificate : </label>&nbsp; &nbsp;
                  <Input type="text" name="maidPoliceVerificationCertificate" value={(JSON.parse(localStorage.getItem('updatemaid')).maidPoliceVerificationCertificate)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Extra Charge Per Room : </label>&nbsp; &nbsp;
                  <Input type="text" name="maidExtraChargePerRoom" value={(JSON.parse(localStorage.getItem('updatemaid')).maidExtraChargePerRoom)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Extra Charge Per Member:</label>&nbsp; &nbsp;
                  <Input type="text" name="maidExtraChargePerMember:" value={(JSON.parse(localStorage.getItem('updatemaid')).maidExtraChargePerMember)} disabled={true} noValidate required />

                </InputGroup>
              </div>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <InputGroup className="mb-2">
                  <label style={{ display: 'flex', justifyContent: 'left', alignItems: 'left' }}>Experience : </label>&nbsp; &nbsp;
                  <Input type="text" name="maidExperience" value={(JSON.parse(localStorage.getItem('updatemaid')).maidExperience)} disabled={true} noValidate required />

                </InputGroup>
              </div>

              <Button onClick={(event) => { this.update(event) }} color="success" >UPDATE</Button>
            </Form>
          </Col>
        </center>
        <Routes>
          <Route path="/MaidHome" element={<MaidHome />} />
        </Routes>
      </div>
    );

  }

}
export default UpdateMaid;
